CREATE TABLE AUTOR (ID NUMBER(19) NOT NULL, APELLIDOS VARCHAR2(255) NULL, FECHANACIMIENTO DATE NULL, INDICEH NUMBER(10) NULL, NOMBRE VARCHAR2(255) NULL, PRIMARY KEY (ID))
CREATE TABLE ARTICULO (ID NUMBER(19) NOT NULL, ANIO NUMBER(10) NULL, NUMERO NUMBER(10) NULL, PAGINAFINAL NUMBER(10) NULL, PAGINAINICIAL NUMBER(10) NULL, PUBLICACION VARCHAR2(255) NULL, TITULO VARCHAR2(255) NULL, VOLUMEN NUMBER(10) NULL, PRIMARY KEY (ID))
CREATE TABLE Autor_FIRMAS (Autor_ID NUMBER(19) NULL, FIRMAS VARCHAR2(255) NULL)
CREATE TABLE ARTICULO_AUTOR (Articulo_ID NUMBER(19) NOT NULL, autores_ID NUMBER(19) NOT NULL, PRIMARY KEY (Articulo_ID, autores_ID))
ALTER TABLE Autor_FIRMAS ADD CONSTRAINT FK_Autor_FIRMAS_Autor_ID FOREIGN KEY (Autor_ID) REFERENCES AUTOR (ID)
ALTER TABLE ARTICULO_AUTOR ADD CONSTRAINT FK_ARTICULO_AUTOR_Articulo_ID FOREIGN KEY (Articulo_ID) REFERENCES ARTICULO (ID)
ALTER TABLE ARTICULO_AUTOR ADD CONSTRAINT FK_ARTICULO_AUTOR_autores_ID FOREIGN KEY (autores_ID) REFERENCES AUTOR (ID)
